parameters.jsCode:
return items.map(item => {
  let parsedData = {};
  let output = item.json.output;

  if (typeof output !== 'string') {
    output = String(output || '');
  }

  // Limpieza básica de caracteres problemáticos
  output = output
    .replace(/\\n/g, "\\n")  // mantener los \n escapados
    .replace(/\\r/g, "\\r")
    .replace(/\r?\n|\r/g, "") // quitar saltos de línea no escapados
    .replace(/\t/g, "\\t")
    .replace(/[\u0000-\u001F]+/g, ""); // quitar caracteres de control no imprimibles

  // Intentar parsear
  try {
    parsedData = JSON.parse(output);
  } catch (error) {
    // Si falla, intentar extraer con expresiones regulares como respaldo
    const subjectMatch = output.match(/"Subject"\s*:\s*"([^"]+)"/);
    const mensajeMatch = output.match(/"Mensaje"\s*:\s*"([\s\S]*?)",\s*"nextMail"/);

    return {
      json: {
        Subject: subjectMatch ? subjectMatch[1] : '',
        Mensaje: mensajeMatch ? mensajeMatch[1].replace(/\\"/g, '"') : '',
        error: 'JSON inválido (uso regex)',
        errorMessage: error.message,
        rawOutput: output,
      }
    };
  }

  const subject = parsedData.Subject || parsedData.subject || '';
  const mensaje = parsedData.Mensaje || parsedData.mensaje || parsedData.message || '';

  return {
    json: {
      Subject: subject,
      Mensaje: mensaje,
    }
  };
});

function countBusinessDays(startDate, endDate) {
  let count = 0;
  let currentDate = new Date(startDate);
  currentDate.setHours(0, 0, 0, 0);
  endDate = new Date(endDate);
  endDate.setHours(0, 0, 0, 0);

  while (currentDate <= endDate) {
    const day = currentDate.getDay();
    if (day !== 0 && day !== 6) count++;
    currentDate.setDate(currentDate.getDate() + 1);
  }
  return count;
}

const hoy = new Date();
hoy.setHours(0, 0, 0, 0);

return items
  .map(item => {
    const estatus = (item.json.ESTATUS || '').trim().toLowerCase();
    const actualizacion = new Date(item.json["ACTUALIZACIÓN"]);

    if (isNaN(actualizacion)) {
      // Si la fecha es inválida, omite este item
      return null;
    }

    actualizacion.setHours(0, 0, 0, 0);

    let nextEmail = null;

    if (estatus === 'mail valido' || estatus === 'vacaciones') {
      nextEmail = 'Mail 1';
    } else if (estatus === 'mail 1' && countBusinessDays(actualizacion, hoy) >= 3) {
      nextEmail = 'Mail 2';
    } else if (estatus === 'mail 2' && countBusinessDays(actualizacion, hoy) >= 3) {
      nextEmail = 'Mail 3';
    } else if (estatus === 'duplicado') {
      nextEmail = 'duplicado';
    }

    if (nextEmail) {
      return {
        json: {
          ...item.json,
          nextEmail,
        }
      };
    }

    return null;
  })
  .filter(item => item !== null);

