return items.map(item => {
  const data = JSON.parse(item.json.output);   // ← desempaque
  return { json: data };                       // ← solo las 4 llaves limpias
});

function countBusinessDays(startDate, endDate) {
  let count = 0;
  let currentDate = new Date(startDate);
  while (currentDate <= endDate) {
    const day = currentDate.getDay();
    if (day !== 0 && day !== 6) count++;
    currentDate.setDate(currentDate.getDate() + 1);
  }
  return count;
}

const hoy = new Date();

return items
  .map(item => {
    const estatus = (item.json.ESTATUS || '').trim().toLowerCase();
    const actualizacion = new Date(item.json.ACTUALIZACIÓN);
    let nextEmail = null;

    if (estatus === 'calificado' || estatus === 'vacaciones') {
      nextEmail = 'Mail 1';
    } else if (estatus === 'mail 1' && countBusinessDays(actualizacion, hoy) >= 3) {
      nextEmail = 'Mail 2';
    } else if (estatus === 'mail 2' && countBusinessDays(actualizacion, hoy) >= 3) {
      nextEmail = 'Mail 3';
    } else if (estatus === 'duplicado') {
      nextEmail = 'duplicado';
    }

    if (nextEmail) {
      return {
        json: {
          ...item.json,
          nextEmail,
        }
      };
    }

    return null;
  })
  .filter(item => item !== null);

